    function balancerV2WeightedPoolState(
        address vault,
        bytes32 poolId,
        address pool
    )
        external
        view
        override
        returns (
            IERC20[] memory tokens,
            uint256[] memory balances,
            uint256 totalSupply,
            uint256[] memory weights,
            uint256 swapFee
